package hackerRank;

import java.util.Scanner;

public class SubstringPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 Scanner in = new Scanner(System.in);
	        String s = in.next();
	        int a = in.nextInt();
	        int b = in.nextInt();
	        
		System.out.println(s.substring(a,b));
		
		
	}

}
